mapper.update:
UPDATE TBL_USER
   SET USER_PWD = #{userPwd},
       USER_NAME = #{userName},
       USER_NICKNAME = #{userNickname}
 WHERE USER_ID = #{userId}

UPDATE TBL_PRODUCT
   SET PRODUCT_STATUS = 'N'
 WHERE PRODUCT_SEQ = #{productSeq}
   AND USER_SELLER_ID = #{userSellerId}
   AND PRODUCT_STATUS = 'Y'

mapper.select:
SELECT COUNT(PRODUCT_SEQ)
  FROM TBL_CART
 WHERE USER_ID = #{value}

SELECT COUNT(QNA_SEQ)
  FROM TBL_QNA
 WHERE USER_ID = #{value}

SELECT COUNT(REVIEW_SEQ)
  FROM TBL_REVIEW
 WHERE USER_ID = #{value}

SELECT COUNT(REPORT_SEQ)
  FROM TBL_REPORT
 WHERE USER_ID = #{value}

SELECT FR_STATUS
  FROM TBL_FRIEND
 WHERE USER_ID = #{userId} 
   AND FR_USER_ID = #{frUserId}

mapper.update:
UPDATE TBL_FRIEND
   SET FR_STATUS = '4'
 WHERE USER_ID = #{frUserId}
   AND FR_USER_ID = #{userId}

UPDATE TBL_FRIEND
   SET FR_STATUS = '4'
 WHERE USER_ID = #{userId}
   AND FR_USER_ID = #{frUserId}

UPDATE TBL_USER
   SET POINT_POS = #{pointPos}
 WHERE USER_ID = #{userId}

mapper.delete:
DELETE FROM TBL_FRIEND
 WHERE USER_ID = #{frUserId}
   AND FR_USER_ID = #{userId}

DELETE FROM TBL_FRIEND
 WHERE USER_ID = #{userId}
   AND FR_USER_ID = #{frUserId}

